import { useEffect, useState } from "react";

function Museums(){
    const url = "https://back-museums-uniandes.herokuapp.com/api/museums"

    const[museums, setMuseums] = useState([]);

    useEffect(()=>{
        fetch (url).then(res=>res.json()).then(museums=>{
            setMuseums(museums);
        })
    })
    return(
        <div className="div-lista-museos">
        <div className="row">
        {museums.map(museum=>{
            <div className="col-3">
            <div className="rectangulo-museo">
              <div className="imagen-museo">
                <img
                  className="img-fluid"
                  src= {museum.image} alt={"foto del museo "+museum.name}/>
              </div>
              <div className="nombre-museo">{museum.name}</div>
              <div className="ciudad-museo">{museum.city}</div>
            </div>
          </div>
        })}

        </div>
      </div>
    )
}

export default Museums;
