import { useEffect, useState } from "react";

function Museums() {
  const url = "https://back-museums-uniandes.herokuapp.com/api/museums";

  const [museums, setMuseums] = useState([]);

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((museums) => {
        setMuseums(museums);
      });
  });
  return (
    <div>
        <div id="lista-museos" className="div-lista-museos">
          <div className="row">
            {museums.map((museum) => {
              return <div className="col-3">
                <div onClick={()=>{abrir(museum.id)}} id={'museo_'+museum.id} className="rectangulo-museo">
                  <div className="imagen-museo">
                    <img
                      className="img-fluid"
                      src={museum.image}
                      alt={"foto del museo " + museum.name}
                    />
                  </div>
                  <div className="nombre-museo">{museum.name}</div>
                  <div className="ciudad-museo">{museum.city}</div>
                </div>
              </div>;
            })}
          </div>
        </div>
        <div class="div-museo-detallado">
        <div class="row">
          <div class="col-3">
            <div class="rectangulo-obra">
              <div class="imagen-museo">
                <img
                  class="img-fluid"
                  src="https://img.freepik.com/vector-gratis/dibujo-linea-continua-rostro-femenino-caprichoso-arte-linea-abstraccion-silueta-mujer_121070-406.jpg"
                  alt="Obra de arte generica"
                />
              </div>
              <div class="nombre-museo">Museo 1</div>
            </div>
          </div>
          <div class="col-9">
            <div class="detalles-museo">
              <p class="texto-detalle-museo">Hola</p>
            </div>
          </div>
        </div>
          </div>
    </div>
  );
}

function abrir(idMuseo){
    document.getElementById("lista-museos").classList.add("d-none")
}

export default Museums;
